#!/usr/bin/env python

template_dict = {
    "TPL_ANACONDA_TOKEN": "",
    "TPL_GITHUB_TOKEN": "",
    "PROJECT_NAME": "cellcutoff",
    "PYDIR": "python-cellcutoff",
    "CONDA_PKG_NAME_CPP": "cellcutoff",
    "CONDA_PKG_NAME_PY": "python-cellcutoff",
    "GITHUB_REPO_NAME": "theochem/cellcutoff",
    "TPL_DEPENDENCIES": "conda install -c theochem cellcutoff python-cellcutoff"}

import sys
from string import Template

template_fn = sys.argv[1]

with open(template_fn) as fh:
    l = fh.read()

t = Template(l)
s = t.safe_substitute(**template_dict)
with open(".travis.yml", "w") as fh:
    fh.write(
        "#\n# THIS FILE IS AUTOGENERATED. DO NOT EDIT DIRECTLY. CHANGES IN \".travis.yml.tpl\" INSTEAD.\n#\n")
    fh.write(s)
